import java.util.Arrays;public class ArraysExercises {    public static void main(String[] args) {        int[] numbers = {1, 2, 3, 4, 5};        System.out.println(numbers);        System.out.println(Arrays.toString(numbers));        Person[] person = new Person[3];        person[0] = new Person("Savanna");        person[1] = new Person("Mark");        person[2] = new Person("Marky");        for(Person element : person){            System.out.println(element.getName());        }        Person[] personToAdd = addPerson(person, new Person("Killer"));        for(Person element : personToAdd){            System.out.println(element.getName());        }    }    public static Person[] addPerson(Person[] array, Person newName){        array = Arrays.copyOf(array, array.length + 1);        array[array.length - 1] = newName;        return array;    }}